mapper.select:
select code.com_sub_nm as productName, prod.cnt
from (
	select com_cd, com_sub_cd, count(1) as cnt from mall.tbl_prod_master group by com_cd, com_sub_cd
) prod
left join mall.tbl_comcode_sub code on prod.com_cd = code.com_cd and prod.com_sub_cd = code.com_sub_cd
order by cnt desc, productName;
